import React from "react"

export default class Movie extends React.Component{
    constructor(props){
        super(props);
        //初始化
        this.state = {
            message:"我是一个消息",
            flag:true
        }
    }

    changeMessage(){
        //修改，重新设置
        //setState会引起render视图重绘
        console.log("wrong");
        this.setState({
            message:"I am new message",
            flag: !this.state.flag
        });
    }



    render(){
        const msg = "haha";
        return(

            <div>
                <p>{msg}</p>
                <p>{ this.state.flag }</p>
                {
                    flag?
                    <div>孙悟空</div>:
                    <div>六耳猕猴</div>
                }
                movie:
                <button onClick = {this.changeMessage.bind(this)}>按钮</button>
            </div>
        )
    }
}
