import React from "react"

export default class Movie extends React.Component{
    constructor(props){
        super(props);
        //初始化
        this.state = {
            message:"我是一个消息",
            flag:true
        }
        this.changeMessage = this.changeMessage.bind(this);
        this.changeFlag = this.changeFlag.bind(this)
    }

    changeMessage(){
        //修改，重新设置
        //setState会引起render视图重绘
        console.log("wrong");
        this.setState({
            message:"I am new message",
        });
    }

    changeFlag(){
        this.setState({
            flag:!this.state.flag
        })

    }

    render(){
        //结构赋值
        //优化：事件的写法 
        const {message,flag} = this.state;
        return(

            <div>
                <p>{message}</p>
                <button onClick = {this.changeMessage}>按钮</button>
                <p>{ this.state.flag }</p>
                {
                    flag ?
                    <div>孙悟空</div>:
                    <div>六耳猕猴</div>
                }
                <button onClick = {this.changeFlag}>修改</button>
                movie:

            </div>
        )
    }
}
